const Course = (props) =>{  

  const Header = ({ course }) => <h1>{course}</h1>

const Total = ({ total }) =>
  <p>
    Total of {total} exercises
  </p>

const Part = ({ part }) => 
  <p>
    {part.name} {part.exercises}
  </p>

const Content = ({ parts }) => 
  <>
    {parts.map(part =>
      <Part key={part.id} part = {part}/>
    )}     
  </>

  const total = 
  props.course.parts.map(ex => ex.exercises).reduce((s,p) => s+p, 0)


  return <>
    <Header course = {props.course.name}/>
    <Content parts = {props.course.parts}/>
    <Total total = {total}/>
  </>}


const App = () => {
  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    }, 
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]

  return   <>
  {courses.map(course =>
    <Course key={course.id} course = {course}/>
  )}     
</>
}

export default App
